import React, { useState, useCallback } from 'react'

/**
 * Renders an array of strings passed in that can be filtered and added to as an
 * unordered list.
 * @returns Component
 */
export default function Sidebar({ initialMenuItems = ["Home", "About", "Contact"] }) {
  let [newMenuItem, setNewMenuItem] = useState("")
  // Use a state hook to maintain the current menu items as an array
  let [menuItems, setMenuItems] = useState(initialMenuItems)
  let [filter, setFilter] = useState("")

  // Adds a single string passed in as parameter to the state element
  // "menuItems" that holds the set of current menu items.
  let addMenuItem = useCallback(() => {
    if (newMenuItem.trim() === "") return; // Prevent adding empty items
    // Prepend the new item (or change to append, if preferred)
    setMenuItems([newMenuItem, ...menuItems])
    setNewMenuItem("") // Clear input after adding
  }, [newMenuItem, menuItems])

  // Filter the menu items based on the filter text (case-insensitive)
  const filteredItems = menuItems.filter(item =>
    new RegExp(filter, "i").test(item)
  )

  return (
    <div>
      <input
        type="text"
        id="newMenuItemValue"
        value={newMenuItem}
        onChange={(event) => setNewMenuItem(event.target.value)}
      />
      <br />
      <button onClick={addMenuItem}>
        Add Item
      </button>
      <br />
      <input
        id="filter"
        type="text"
        value={filter}
        onChange={(event) => setFilter(event.target.value)}
        placeholder="Filter by..."
      />
      <br />
      <ul>
        {filteredItems.map((item, index) => (
          <li key={index}>{item}</li>
        ))}
      </ul>
    </div>
  )
}
